mapper.update:
UPDATE job_post
SET complaint = complaint + 1
WHERE id = #{postId}

UPDATE job_post
SET page_view = page_view + 1
WHERE id = #{postId}

UPDATE job_post
SET likes = likes + 1
WHERE id = #{postId}

UPDATE
    job_post
SET
    deleted = #{deleted}
WHERE
    id = #{postId}
