mapper.select:
SELECT * FROM private_notification noti
    INNER JOIN member mem ON mem.id = noti.member_id


        AND noti.member_id = #{memberId,jdbcType=INTEGER}


        AND noti.title = #{title,jdbcType=VARCHAR}


        AND noti.is_read = #{isRead,jdbcType=INTEGER}


        AND noti.time = #{time,jdbcType=DATE}


        AND noti.access_range = #{accessRange,jdbcType=VARCHAR}


        AND noti.content = #{content,jdbcType=LONGVARCHAR}



LIMIT
    #{pageNumber}, #{pageSize}


SELECT * FROM private_notification noti
    INNER JOIN member mem ON mem.id = noti.member_id
WHERE
    noti.member_id = #{memberId}
    AND noti.access_range = #{roleName}
    AND noti.time >= #{startTime}
    AND noti.time <= #{endTime}
LIMIT
    #{pageNumber}, #{pageSize}

SELECT COUNT(*) FROM private_notification noti
WHERE
    noti.member_id = #{memberId} AND noti.is_read = #{isRead}
