mapper.insert:

INSERT INTO review (post_id, member_id,
time, replied_review_id, content
)
VALUES (#{postId,jdbcType=INTEGER}, #{memberId,jdbcType=INTEGER},
#{time,jdbcType=DATE}, #{repliedReviewId,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}
)

mapper.select:
SELECT
    ,

FROM
    review rev
LEFT JOIN
    member mem ON mem.id = rev.member_id


        rev.post_id = #{postId,jdbcType=INTEGER}


        AND rev.member_id = #{memberId,jdbcType=INTEGER}


        AND rev.time = #{time,jdbcType=DATE}


        AND rev.replied_review_id = #{repliedReviewId,jdbcType=INTEGER}


        AND rev.content = #{content,jdbcType=LONGVARCHAR}


LIMIT
    #{pageNumber}, #{pageSize}
