mapper.insert:

INSERT INTO school_activity (member_id, school_organization_id,
title, created_time, expired_time,
likes, school_activity_category_id, content
)
VALUES (#{memberId,jdbcType=INTEGER}, #{schoolOrganizationId,jdbcType=INTEGER},
#{title,jdbcType=VARCHAR}, #{createdTime,jdbcType=DATE}, #{expiredTime,jdbcType=DATE},
#{likes,jdbcType=INTEGER}, #{schoolActivityCategoryId,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}
)

mapper.select:
SELECT * FROM school_activity act
    INNER JOIN member mem ON mem.id = act.member_id
    INNER JOIN school_activity_category cate ON cate.id = act.school_activity_category_id


        act.member_id = #{memberId,jdbcType=INTEGER}


        AND act.school_organization_id = #{schoolOrganizationId,jdbcType=INTEGER}


        AND act.title = #{title,jdbcType=VARCHAR}


        AND act.created_time = #{createdTime,jdbcType=DATE}


        AND act.expired_time = #{expiredTime,jdbcType=DATE}


        AND act.likes = #{likes,jdbcType=INTEGER}


        AND act.school_activity_category_id = #{schoolActivityCategoryId,jdbcType=INTEGER}


        AND act.content = #{content,jdbcType=LONGVARCHAR}


LIMIT
    #{pageNumber}, #{pageSize}
ORDER BY
    act.created_time DESC
